        public Config Load() {

            if (Directory.Exists(@"C:\SmallBusinessSuite")) {
                if (File.Exists(@"C:\SmallBusinessSuite\config.json")) {
                    using (StreamReader reader = new StreamReader(@"C:\SmallBusinessSuite\config.json")) {
                        string json = reader.ReadToEnd();
                        Config config = JsonConvert.DeserializeObject<Config>(json);
                        return config;
                    }

                } else {
                    Config config = new Config(true, 100, GenerateDicitonary(), html);
                    Save(config, @"C:\SmallBusinessSuite\config.json");
                    SaveXAML();
                    return config;
                }
            } else {
                Directory.CreateDirectory(@"C:\SmallBusinessSuite");
                Directory.CreateDirectory(@"C:\SmallBusinessSuite\Invoices");

                Config config = new Config(true, 100, GenerateDicitonary(), html);
                Save(config, @"C:\SmallBusinessSuite\config.json");
                SaveXAML();
                return config;
            }
        }

        public void Save(Config config, string destination) {
            using (StreamWriter file = File.CreateText(destination)) {
                using (JsonWriter writer = new JsonTextWriter(file)) {
                    JsonSerializer serializer= new JsonSerializer();
                    serializer.Formatting = Formatting.Indented;
                    serializer.Serialize(writer, config);
                }
            }
        }

        private Dictionary<string, string> GenerateDicitonary() {
            Dictionary<string, string> invoiceInfo = new Dictionary<string, string>();
            invoiceInfo.Add("[COMPANY]", "");
            invoiceInfo.Add("[PHONE]", "");
            invoiceInfo.Add("[EMAIL]", "");
            invoiceInfo.Add("[ADDRESS]", "");
            invoiceInfo.Add("[URL]", "");
            invoiceInfo.Add("[INVOICE_DATE]", "");
            invoiceInfo.Add("[INVOICE_NUM]", "");
            invoiceInfo.Add("[RECIPIENT_COMPANY]", "");
            invoiceInfo.Add("[RECIPIENT_CONTACT]", "");
            invoiceInfo.Add("[SUM]", "");
            invoiceInfo.Add("[INVOICE_APPRECIATION]", "");

            for (int i = 0; i < 10; i++) {
                invoiceInfo.Add($"[DESC{i}]", "");
                invoiceInfo.Add($"[WORK{i}]", "");
                invoiceInfo.Add($"[HOUR{i}]", "");
                invoiceInfo.Add($"[AMNT{i}]", "");
                invoiceInfo.Add($"[RATE{i}]", "");
            }

            return invoiceInfo;
        }

        private void SaveXAML() {
            if (!File.Exists(@"C:\SmallBusinessSuite\InvoiceTemplate.dotx")) {
                using (WordprocessingDocument template = WordprocessingDocument.Create(@"C:\SmallBusinessSuite\InvoiceTemplate.dotx", WordprocessingDocumentType.Template)) {
                    MainDocumentPart main = template.AddMainDocumentPart();

                    using (StreamWriter writer = new StreamWriter(main.GetStream(FileMode.Create))) {
                        writer.Write(xaml);
                    }

                    template.Save();
                }
            }
        }
